import React, { useState, useEffect } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { motion } from 'framer-motion';
import { FiActivity, FiShield, FiWifi, FiCpu, FiSettings, FiMonitor } from 'react-icons/fi';
import Header from './Header';
import PlayfairTab from './tabs/PlayfairTab';
import RIPTab from './tabs/RIPTab';
import TCPTab from './tabs/TCPTab';
import SimulationTab from './tabs/SimulationTab';
import NetworkVisualization from './NetworkVisualization';
import SettingsTab from './tabs/SettingsTab';
import 'react-tabs/style/react-tabs.css';
import './Dashboard.css';

const Dashboard = () => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [systemStats, setSystemStats] = useState({
    cpu: 0,
    memory: 0,
    network: 0,
    status: 'Online'
  });

  // Simulate system stats
  useEffect(() => {
    const interval = setInterval(() => {
      setSystemStats({
        cpu: Math.floor(Math.random() * 100),
        memory: Math.floor(Math.random() * 100),
        network: Math.floor(Math.random() * 100),
        status: 'Online'
      });
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const tabItems = [
    { id: 'overview', label: 'Overview', icon: FiMonitor },
    { id: 'playfair', label: 'Playfair Cipher', icon: FiShield },
    { id: 'rip', label: 'RIP Routing', icon: FiWifi },
    { id: 'tcp', label: 'TCP Reno', icon: FiActivity },
    { id: 'simulation', label: 'Full Simulation', icon: FiCpu },
    { id: 'settings', label: 'Settings', icon: FiSettings }
  ];

  return (
    <div className="dashboard">
      <Header systemStats={systemStats} />

      <div className="dashboard-content">
        <Tabs 
          selectedIndex={selectedTab} 
          onSelect={setSelectedTab}
          className="dashboard-tabs"
        >
          <TabList className="tab-list">
            {tabItems.map((tab, index) => (
              <Tab 
                key={tab.id} 
                className={`tab-item ${selectedTab === index ? 'active' : ''}`}
              >
                <motion.div
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="tab-content"
                >
                  <tab.icon className="tab-icon" />
                  <span className="tab-label">{tab.label}</span>
                </motion.div>
              </Tab>
            ))}
          </TabList>

          <div className="tab-panels">
            <TabPanel className="tab-panel">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <NetworkVisualization />
              </motion.div>
            </TabPanel>

            <TabPanel className="tab-panel">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <PlayfairTab />
              </motion.div>
            </TabPanel>

            <TabPanel className="tab-panel">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <RIPTab />
              </motion.div>
            </TabPanel>

            <TabPanel className="tab-panel">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <TCPTab />
              </motion.div>
            </TabPanel>

            <TabPanel className="tab-panel">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <SimulationTab />
              </motion.div>
            </TabPanel>

            <TabPanel className="tab-panel">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <SettingsTab />
              </motion.div>
            </TabPanel>
          </div>
        </Tabs>
      </div>
    </div>
  );
};

export default Dashboard;
