import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FiPlay, FiRefreshCw, FiBarChart, FiSettings, FiActivity, FiTrendingUp } from 'react-icons/fi';
import axios from 'axios';
import './TCPTab.css';

const TCPTab = () => {
  const [networkData, setNetworkData] = useState({ nodes: [], links: [] });
  const [config, setConfig] = useState({
    data_size: 10000,
    mss: 1460,
    initial_cwnd: 1,
    ssthresh: 65535,
    packet_loss_rate: 0.05,
    max_time: 100,
    source_node: '',
    destination_node: ''
  });

  const [results, setResults] = useState(null);
  const [plotImage, setPlotImage] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchNetworkData();
  }, []);

  useEffect(() => {
    // Set default source and destination when network data changes
    if (networkData.nodes.length > 0 && !config.source_node) {
      setConfig(prev => ({
        ...prev,
        source_node: networkData.nodes[0].id
      }));
    }
    if (networkData.nodes.length > 1 && !config.destination_node) {
      setConfig(prev => ({
        ...prev,
        destination_node: networkData.nodes[networkData.nodes.length - 1].id
      }));
    }
  }, [networkData, config.source_node, config.destination_node]);

  const fetchNetworkData = async () => {
    try {
      const response = await axios.get('/api/network/topology');
      if (response.data.success) {
        setNetworkData(response.data);
      }
    } catch (err) {
      console.error('Network fetch error:', err);
    }
  };

  const handleConfigChange = (key, value) => {
    setConfig(prev => ({
      ...prev,
      [key]: parseFloat(value) || (key.includes('node') ? value : 0)
    }));
  };

  const runSimulation = async () => {
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('/api/tcp/simulate', config);

      if (response.data.success) {
        setResults(response.data.results);
        setPlotImage(response.data.plot);
      } else {
        setError(response.data.error || 'Simulation failed');
      }
    } catch (err) {
      setError('Error connecting to API');
      console.error('TCP simulation error:', err);
    } finally {
      setLoading(false);
    }
  };

  const resetConfig = () => {
    setConfig({
      data_size: 10000,
      mss: 1460,
      initial_cwnd: 1,
      ssthresh: 65535,
      packet_loss_rate: 0.05,
      max_time: 100,
      source_node: networkData.nodes.length > 0 ? networkData.nodes[0].id : '',
      destination_node: networkData.nodes.length > 1 ? networkData.nodes[networkData.nodes.length - 1].id : ''
    });
    setResults(null);
    setPlotImage('');
  };

  const getStateColor = (state) => {
    switch (state) {
      case 'slow_start': return '#4ecdc4';
      case 'congestion_avoidance': return '#ffe66d';
      case 'fast_recovery': return '#ff6b6b';
      default: return '#6c7293';
    }
  };

  const calculateStats = () => {
    if (!results) return null;

    const totalTime = results.time_taken || 0;
    const totalPackets = Math.ceil(config.data_size / config.mss);
    const throughput = totalTime > 0 ? (config.data_size * 8) / (totalTime * 1000) : 0; // Mbps
    const packetLossEvents = results.packet_loss_events || 0;
    const avgCwnd = results.cwnd_history ? 
      results.cwnd_history.reduce((a, b) => a + b, 0) / results.cwnd_history.length : 0;

    return {
      totalTime: totalTime.toFixed(2),
      throughput: throughput.toFixed(2),
      totalPackets,
      packetLossEvents,
      avgCwnd: avgCwnd.toFixed(1)
    };
  };

  const availableNodes = networkData.nodes.map(node => node.id).sort();
  const stats = calculateStats();

  return (
    <div className="tcp-tab">
      <div className="tab-header">
        <div className="header-content">
          <h2>TCP Reno Simulation</h2>
          <p>Congestion Control Algorithm - Slow Start, Congestion Avoidance & Fast Recovery</p>
        </div>
        <div className="header-actions">
          <motion.button
            className="action-btn secondary"
            onClick={resetConfig}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FiRefreshCw /> Reset
          </motion.button>
        </div>
      </div>

      {error && (
        <motion.div 
          className="error-message"
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
        >
          {error}
        </motion.div>
      )}

      <div className="tcp-content">
        <div className="simulation-setup">
          <div className="setup-section">
            <h3><FiSettings /> Network Configuration</h3>
            {availableNodes.length > 0 ? (
              <div className="network-config">
                <div className="node-selectors">
                  <div className="node-selector">
                    <label>Source Node</label>
                    <select
                      value={config.source_node}
                      onChange={(e) => handleConfigChange('source_node', e.target.value)}
                      className="cyber-select"
                    >
                      <option value="">Select Source</option>
                      {availableNodes.map(node => (
                        <option key={node} value={node}>Node {node}</option>
                      ))}
                    </select>
                  </div>
                  <div className="connection-arrow">→</div>
                  <div className="node-selector">
                    <label>Destination Node</label>
                    <select
                      value={config.destination_node}
                      onChange={(e) => handleConfigChange('destination_node', e.target.value)}
                      className="cyber-select"
                    >
                      <option value="">Select Destination</option>
                      {availableNodes.map(node => (
                        <option key={node} value={node}>Node {node}</option>
                      ))}
                    </select>
                  </div>
                </div>
                <div className="network-info">
                  <span className="info-item">
                    <strong>Total Nodes:</strong> {networkData.nodes.length}
                  </span>
                  <span className="info-item">
                    <strong>Total Links:</strong> {networkData.links.length}
                  </span>
                </div>
              </div>
            ) : (
              <div className="no-network">
                <p>No network topology found. Please create a network first.</p>
                <motion.button
                  className="action-btn primary small"
                  onClick={fetchNetworkData}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <FiRefreshCw /> Refresh
                </motion.button>
              </div>
            )}
          </div>

          <div className="setup-section">
            <h3><FiSettings /> Simulation Parameters</h3>

            <div className="controls-grid">
              <div className="control-group">
                <label>Data Size (bytes)</label>
                <input
                  type="number"
                  value={config.data_size}
                  onChange={(e) => handleConfigChange('data_size', e.target.value)}
                  className="cyber-input"
                  min="1"
                  max="1000000"
                />
                <span className="control-hint">Amount of data to transmit</span>
              </div>

              <div className="control-group">
                <label>MSS (bytes)</label>
                <input
                  type="number"
                  value={config.mss}
                  onChange={(e) => handleConfigChange('mss', e.target.value)}
                  className="cyber-input"
                  min="1"
                  max="9000"
                />
                <span className="control-hint">Maximum Segment Size</span>
              </div>

              <div className="control-group">
                <label>Initial CWND</label>
                <input
                  type="number"
                  value={config.initial_cwnd}
                  onChange={(e) => handleConfigChange('initial_cwnd', e.target.value)}
                  className="cyber-input"
                  min="1"
                  max="100"
                />
                <span className="control-hint">Initial congestion window size</span>
              </div>

              <div className="control-group">
                <label>SSThresh</label>
                <input
                  type="number"
                  value={config.ssthresh}
                  onChange={(e) => handleConfigChange('ssthresh', e.target.value)}
                  className="cyber-input"
                  min="1"
                  max="100000"
                />
                <span className="control-hint">Slow start threshold</span>
              </div>

              <div className="control-group">
                <label>Packet Loss Rate</label>
                <input
                  type="number"
                  step="0.01"
                  value={config.packet_loss_rate}
                  onChange={(e) => handleConfigChange('packet_loss_rate', e.target.value)}
                  className="cyber-input"
                  min="0"
                  max="1"
                />
                <span className="control-hint">Probability of packet loss (0-1)</span>
              </div>

              <div className="control-group">
                <label>Max Time Steps</label>
                <input
                  type="number"
                  value={config.max_time}
                  onChange={(e) => handleConfigChange('max_time', e.target.value)}
                  className="cyber-input"
                  min="10"
                  max="1000"
                />
                <span className="control-hint">Maximum simulation duration</span>
              </div>
            </div>

            <motion.button
              className="action-btn primary large run-simulation"
              onClick={runSimulation}
              disabled={loading || !config.source_node || !config.destination_node}
              whileHover={{ scale: (loading || !config.source_node || !config.destination_node) ? 1 : 1.05 }}
              whileTap={{ scale: (loading || !config.source_node || !config.destination_node) ? 1 : 0.95 }}
            >
              {loading ? (
                <>
                  <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                  >
                    <FiRefreshCw />
                  </motion.div>
                  Running Simulation...
                </>
              ) : (
                <>
                  <FiPlay /> Run TCP Simulation
                </>
              )}
            </motion.button>
          </div>
        </div>

        {/* Results Section */}
        {results && (
          <motion.div 
            className="results-section"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <h3><FiBarChart /> Simulation Results</h3>

            {/* Statistics Cards */}
            {stats && (
              <div className="stats-cards">
                <div className="stat-card">
                  <div className="stat-icon">
                    <FiActivity />
                  </div>
                  <div className="stat-content">
                    <div className="stat-value">{stats.totalTime}s</div>
                    <div className="stat-label">Total Time</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">
                    <FiTrendingUp />
                  </div>
                  <div className="stat-content">
                    <div className="stat-value">{stats.throughput} Mbps</div>
                    <div className="stat-label">Throughput</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">
                    <FiBarChart />
                  </div>
                  <div className="stat-content">
                    <div className="stat-value">{stats.totalPackets}</div>
                    <div className="stat-label">Total Packets</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">
                    <FiRefreshCw />
                  </div>
                  <div className="stat-content">
                    <div className="stat-value">{stats.packetLossEvents}</div>
                    <div className="stat-label">Loss Events</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">
                    <FiSettings />
                  </div>
                  <div className="stat-content">
                    <div className="stat-value">{stats.avgCwnd}</div>
                    <div className="stat-label">Avg CWND</div>
                  </div>
                </div>
              </div>
            )}

            {/* Plot Visualization */}
            {plotImage && (
              <div className="plot-container">
                <h4>Congestion Window Evolution</h4>
                <div className="plot-wrapper">
                  <img 
                    src={`data:image/png;base64,${plotImage}`} 
                    alt="TCP Reno Simulation Plot"
                    className="simulation-plot"
                  />
                </div>
              </div>
            )}

            {/* State Timeline */}
            {results.state_history && (
              <div className="state-timeline">
                <h4>Algorithm State Timeline</h4>
                <div className="timeline-container">
                  {results.state_history.map((state, index) => (
                    <div 
                      key={index}
                      className="timeline-item"
                      style={{ backgroundColor: getStateColor(state) }}
                      title={`Time ${index}: ${state.replace('_', ' ').toUpperCase()}`}
                    />
                  ))}
                </div>
                <div className="timeline-legend">
                  <div className="legend-item">
                    <div className="legend-color" style={{ backgroundColor: getStateColor('slow_start') }}></div>
                    <span>Slow Start</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color" style={{ backgroundColor: getStateColor('congestion_avoidance') }}></div>
                    <span>Congestion Avoidance</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color" style={{ backgroundColor: getStateColor('fast_recovery') }}></div>
                    <span>Fast Recovery</span>
                  </div>
                </div>
              </div>
            )}
          </motion.div>
        )}
      </div>

      <div className="tcp-info">
        <h3>TCP Reno Algorithm</h3>
