import React from 'react';
import { motion } from 'framer-motion';
import { FiWifi, FiCpu, FiHardDrive, FiActivity } from 'react-icons/fi';
import './Header.css';

const Header = ({ systemStats }) => {
  const getStatusColor = (status) => {
    switch (status) {
      case 'Online': return '#4ecdc4';
      case 'Warning': return '#ffe66d';
      case 'Error': return '#ff6b6b';
      default: return '#6c7293';
    }
  };

  const getUsageColor = (usage) => {
    if (usage < 50) return '#4ecdc4';
    if (usage < 80) return '#ffe66d';
    return '#ff6b6b';
  };

  return (
    <motion.header 
      className="dashboard-header"
      initial={{ y: -80, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
    >
      <div className="header-content">
        <div className="header-left">
          <motion.div 
            className="logo-section"
            whileHover={{ scale: 1.05 }}
          >
            <div className="logo-icon">
              <FiActivity className="pulse-effect" />
            </div>
            <div className="logo-text">
              <h1>DCN Dashboard</h1>
              <span className="logo-subtitle">Network Simulation Hub</span>
            </div>
          </motion.div>
        </div>

        <div className="header-center">
          <motion.div 
            className="status-indicator"
            animate={{ 
              boxShadow: `0 0 20px ${getStatusColor(systemStats.status)}40`
            }}
            transition={{ duration: 2, repeat: Infinity, repeatType: "reverse" }}
          >
            <div 
              className="status-dot"
              style={{ backgroundColor: getStatusColor(systemStats.status) }}
            />
            <span className="status-text">{systemStats.status}</span>
          </motion.div>
        </div>

        <div className="header-right">
          <div className="system-stats">
            <motion.div 
              className="stat-item"
              whileHover={{ scale: 1.1 }}
            >
              <FiCpu className="stat-icon" />
              <div className="stat-info">
                <span className="stat-label">CPU</span>
                <div className="stat-bar">
                  <div 
                    className="stat-fill"
                    style={{ 
                      width: `${systemStats.cpu}%`,
                      backgroundColor: getUsageColor(systemStats.cpu)
                    }}
                  />
                </div>
                <span 
                  className="stat-value"
                  style={{ color: getUsageColor(systemStats.cpu) }}
                >
                  {systemStats.cpu}%
                </span>
              </div>
            </motion.div>

            <motion.div 
              className="stat-item"
              whileHover={{ scale: 1.1 }}
            >
              <FiHardDrive className="stat-icon" />
              <div className="stat-info">
                <span className="stat-label">RAM</span>
                <div className="stat-bar">
                  <div 
                    className="stat-fill"
                    style={{ 
                      width: `${systemStats.memory}%`,
                      backgroundColor: getUsageColor(systemStats.memory)
                    }}
                  />
                </div>
                <span 
                  className="stat-value"
                  style={{ color: getUsageColor(systemStats.memory) }}
                >
                  {systemStats.memory}%
                </span>
              </div>
            </motion.div>

            <motion.div 
              className="stat-item"
              whileHover={{ scale: 1.1 }}
            >
              <FiWifi className="stat-icon" />
              <div className="stat-info">
                <span className="stat-label">NET</span>
                <div className="stat-bar">
                  <div 
                    className="stat-fill"
                    style={{ 
                      width: `${systemStats.network}%`,
                      backgroundColor: getUsageColor(systemStats.network)
                    }}
                  />
                </div>
                <span 
                  className="stat-value"
                  style={{ color: getUsageColor(systemStats.network) }}
                >
                  {systemStats.network}%
                </span>
              </div>
            </motion.div>
          </div>

          <div className="header-actions">
            <motion.button 
              className="action-button"
              whileHover={{ scale: 1.05, boxShadow: "0 0 15px rgba(0, 212, 255, 0.5)" }}
              whileTap={{ scale: 0.95 }}
            >
              <FiActivity />
            </motion.button>
          </div>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
